mapper.select:
		 select p.* from 
         uspto_patent_info p left join  users_version uv 
         on p.version=uv.version where uv.userid=#{userId} 
         order by patent_number LIMIT #{pageNum},#{pageSize}

		 select count(*) from 
         uspto_patent_info p left join  users_version uv 
         on p.version=uv.version where uv.userid=#{userId}
